import React, { useEffect, useState } from "react";
import axios from "axios";


const UpdateEmployee = (empId,)=>{
    alert("Hello")
    const [Employee,setEmployee] = useState(null) // return an array with two element
      const[loading, setLoading] = useState(true)
      const[error, setError] = useState(null)
      
      useEffect(()=>{
        const fetchEmployee = async () =>{
            setLoading(true)
            try{
                //  npm install axios
                // using axios we send get http request:
                const response = await axios.get(`/api/employees/${empId}`);
                setEmployee(response.data)
                
            }catch(error){
                setError("Error fetching employee data.")
            }
            finally{
                setLoading(false)
            }
        };
        if(empId){
            fetchEmployee()
        }
      },[empId]); 

      const handlChange = (e)=>{
        const {name,value} = e.target;
        setEmployee((prev)=>({...prev, [name]:value}));
      };

      const handlSubmit = async (e)=>{
        e.preventDefault();
        try{
            await axios.put(`/api/employees/${empId}`,Employee)
            ontimeupdate();
            onclose();
        }catch(error){
            console.error(error);
            alert(error)
        }
      };

      if(loading) {
        <div className="text-center mt-5">Loading...</div>
      }
      if(error){
        return <div className="alert alert-danger text-center">{error}</div>
      }
      
    return(
        <div className="text-danger">Update Employee</div>
        
    )
}
export default UpdateEmployee
