import React, { useEffect, useState } from "react";
import axios from "axios";

const UpdateEmployee = ({ empId, onClose, onUpdate }) => {

    const [employee, setEmployee] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch the employee data by ID
    useEffect(() => {
        const fetchEmployee = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`/api/employees/${empId}`);
                setEmployee(response.data);
            } catch (error) {
                setError("Error fetching employee data.");
            } finally {
                setLoading(false);
            }
        };
        if (empId) {
            fetchEmployee();
        }
    }, [empId]);

    // Handle form field changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee((prev) => ({ ...prev, [name]: value }));
    };

    // Handle form submission to update employee data
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.patch(`/api/employees/${empId}`, employee);
            onUpdate(); // Refresh the employee list
            onClose();  // Close the modal
        } catch (error) {
            console.error(error);
            alert("Error updating employee.");
        }
    };

    if (loading) {
        return <div className="text-center mt-5">Loading...</div>;
    }
    if (error) {
        return <div className="alert alert-danger text-center">{error}</div>;
    }

    return (
        <div className="model fade show" style={{display: 'block',
            background: 'rgb(0,0,0,0.5)'
        }} tabIndex='-1' role="dialog">
            <div className="modal-dialog" role="document">
              <div className="model-content">
                <div className="model-header">
                    <h5 className="model-title">Update Employee</h5>
                    <button type="button" className="close" onClick={onClose}
                    aria-label="Close">
                        <span aria-hidden="true">&time</span>
                    </button>
                </div>
              </div>
            </div>
        </div>
    );
};

export default UpdateEmployee;
