import React, { useEffect, useState } from "react";
import axios from "axios";


const updateEmployee = (empId,)=>{
    const [Employees,setEmployees] = useState([]) // return an array with two element
      const[loading, setLoading] = useState(true)
      const[error, setError] = useState(null)
      useEffect(()=>{
        const fetchEmployees = async () =>{
            setLoading(true)
            try{
                //  npm install axios
                // using axios we send get http request:
                const response = await axios.get("/api/employees/allEmployees");
                setEmployees(response.data)
                
            }catch(error){
                setError("Error fetching employee data.")
            }
            finally{
                setLoading(false)
            }
        }
      })
      
    return(
        <div className="text-danger">Update Employee</div>
        
    )
}
export default updateEmployee
