import React, { useEffect, useState } from "react";
import axios from "axios";

const UpdateEmployee = ({ empId, onClose, onUpdate }) => {

    const [employee, setEmployee] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch the employee data by ID
    useEffect(() => {
        const fetchEmployee = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`/api/employees/${empId}`);
                setEmployee(response.data);
            } catch (error) {
                setError("Error fetching employee data.");
            } finally {
                setLoading(false);
            }
        };
        if (empId) {
            fetchEmployee();
        }
    }, [empId]);

    // Handle form field changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee((prev) => ({ ...prev, [name]: value }));
    };

    // Handle form submission to update employee data
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/api/employees/${empId}`, employee);
            onUpdate(); // Refresh the employee list
            onClose();  // Close the modal
        } catch (error) {
            console.error(error);
            alert("Error updating employee.");
        }
    };

    if (loading) {
        return <div className="text-center mt-5">Loading...</div>;
    }
    if (error) {
        return <div className="alert alert-danger text-center">{error}</div>;
    }

    return (
        <div className="model fade show" style={{display: 'block',
            background: 'rgb(0,0,0,'
        }}>
            <div className="modal-content">
                <h2 className="text-center">Update Employee</h2>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label>First Name</label>
                        <input
                            type="text"
                            name="fname"
                            value={employee.fname || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Last Name</label>
                        <input
                            type="text"
                            name="lname"
                            value={employee.lname || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={employee.email || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Department</label>
                        <input
                            type="text"
                            name="department"
                            value={employee.department || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Salary</label>
                        <input
                            type="number"
                            name="salary"
                            value={employee.salary || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group">
                        <label>Number of Leaves</label>
                        <input
                            type="number"
                            name="no_of_leaves"
                            value={employee.no_of_leaves || ""}
                            onChange={handleChange}
                            className="form-control"
                        />
                    </div>
                    <div className="form-group text-center">
                        <button type="submit" className="btn btn-success">
                            Update
                        </button>
                        <button
                            type="button"
                            onClick={onClose}
                            className="btn btn-secondary ml-3"
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default UpdateEmployee;
