import React, { useEffect, useState } from "react"
import axios from "axios"



const EmployeeList = ()=>{
  const [Employees,setEmployees] = useState([]) // return an array with two element
  const[loading, setLoading] = useState(true)
  const[error, setError] = useState(null)
  const fetchEmployees = async () =>{
    setLoading(true)
    try{
        //  npm install axios
        // using axios we send get http request:
        const response = await axios.get("/api/employees/allEmployees");
        setEmployees(response.data)
        
    }catch(error){
        setError("Error fetching employee data.")
    }
    finally{
        setLoading(false)
    }

    
  }
  useEffect(()=>{
    fetchEmployees();
  },[]);
  if(loading) {
    <div className="text-center mt-5">Loading...</div>
  }
  if(error){
    return <div className="alert alert-danger text-center">{error}</div>
  }

  const deleteEmployee = async (empId)=>{
    const conformed = window.confirm("Are you sure you want to delete this employee?")
    if(conformed){
        try{
          const task =  await axios.delete("api/employees/${empId}")
            if(task.response.message === "Deleted Successfully!"){
                alert("Employee Deleted Successfully!")
                fetchEmployees();
            }
        }catch(error){
            console.error(error)
            alert(error)
        }
    }
}

  return (
    <div className="container-fluid  bg-dark" style={{height:"100vh",overflow:"hidden"}}>
       <div className="container">
       <h1 className="text-center mb-4 text-light">Employees List: {Employees.length}</h1>
        <button className="btn btn-primary mb-4" onClick={fetchEmployees}>Refresh</button>
        {Employees.length===0?<p className="text-danger">No Employees Found.</p>:(
            <ul className="list-group">{Employees.map((employee)=>(
                <div className="card mb-3 bg-secondary" key={employee.id}>
                    <div className="card-body pd-5">
                        <div className="d-flex justify-content-between">
                        <h5>{employee.fname} {employee.lname}</h5>
                        <strong className="text-light">{employee.department}</strong>
                        </div>
                        
                        
                        <p>{employee.email}</p>

                        <div className="button-container" style={{display: "flex",gap:"10px"}}>
                        
                            <button type="button" className="btn-danger" onClick={()=> deleteEmployee(employee.id)}>
                                Delete
                            </button>
                        </div>
                    </div>
                </div>
            ))}</ul>
        )}
       </div>
    </div>
  )
}
export default EmployeeList
