import React, { useEffect, useState } from "react"
import axios from "axios"
import updateEmployee from "./updateEmployee";



const EmployeeList = ()=>{
  const [Employees,setEmployees] = useState([]) // return an array with two element
  const[loading, setLoading] = useState(true)
  const[error, setError] = useState(null)
  const[showModel, setShowModel] = useState(false)
  const [selectedEmployeeId,setSelectedEmployeeId] = useState(null);

  const fetchEmployees = async () =>{
    setLoading(true)
    try{
        //  npm install axios
        // using axios we send get http request:
        const response = await axios.get("/api/employees/allEmployees");
        setEmployees(response.data)
        
    }catch(error){
        setError("Error fetching employee data.")
    }
    finally{
        setLoading(false)
    }

    
  }

  const updateEmployee = (empId)=>{
    setSelectedEmployeeId(empId);
    setShowModel(true);
  }
  useEffect(()=>{
    fetchEmployees();
  },[]);
  if(loading) {
    <div className="text-center mt-5">Loading...</div>
  }
  if(error){
    return <div className="alert alert-danger text-center">{error}</div>
  }

  const handleModelClose = ()=>{
    showModel(false);
    setSelectedEmployeeId(null);

  }

  const deleteEmployee = async (empId)=>{
    const conformed = window.confirm("Are you sure you want to delete this employee?")
    if(conformed){
        try{
         await axios.delete(`api/employees/${empId}`)
           
                alert("Employee Deleted Successfully!")
                fetchEmployees();
        }catch(error){
            console.error(error)
            alert(error)
        }
    }
}

  return (
    <div className="container-fluid  bg-dark" style={{height:"100vh",overflowY: "auto"}}>
        <h1 className="text-center mb-4 text-light">Employees List: {Employees.length}</h1>
        <button className="btn btn-primary mb-4" onClick={fetchEmployees}>Refresh</button>
        {Employees.length===0?<p className="text-danger">No Employees Found.</p>:(
            <ul className="list-group">{Employees.map((employee)=>(
                <div className="card mb-3 bg-secondary" key={employee.id}>
                    <div className="card-body pd-5">
                        <div className="d-flex justify-content-between">
                        <h5>{employee.fname} {employee.lname}</h5>
                        <strong className="text-light">{employee.department}</strong>
                        </div>
                        
                        
                        <p>{employee.email}</p>

                        <div className="button-container d-flex justify-content-end bg-secondary" style={{gap:"10px"}}>
                        
                           <button type="button" className="btn btn-outline-success bg-dark" onClick={()=> updateEmployee(employee.id)}>
                                Update
                            </button>
                            <button type="button" className="btn btn-outline-danger bg-dark" onClick={()=> deleteEmployee(employee.id)}>
                                Delete
                            </button>
                          
                        </div>
                    </div>
                </div>
            ))}</ul>
        )}

        {/* {Render the update employee model:} */}
        {showModel  && (
          <updateEmployee empId = {selectedEmployeeId}
          onClose = {handleModelClose}
          onUpdate = {fetchEmployees}
          />
        )}
    </div>
  )
}
export default EmployeeList
