import React, { useEffect, useState } from "react";
import axios from "axios";

const UpdateEmployee = ({ empId, onClose, onUpdate }) => {

    const [employee, setEmployee] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch the employee data by ID
    useEffect(() => {
        const fetchEmployee = async () => {
            setLoading(true);
            try {
                const response = await axios.get(`/api/employees/${empId}`);
                setEmployee(response.data);
            } catch (error) {
                setError("Error fetching employee data.");
            } finally {
                setLoading(false);
            }
        };
        if (empId) {
            fetchEmployee();
        }
    }, [empId]);

    // Handle form field changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setEmployee((prev) => ({ ...prev, [name]: value }));
    };

    // Handle form submission to update employee data
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.patch(`/api/employees/${empId}`, employee);
            onUpdate(); // Refresh the employee list
            onClose();  // Close the modal
        } catch (error) {
            console.error(error);
            alert("Error updating employee.");
        }
    };

    if (loading) {
        return <div className="text-center mt-5">Loading...</div>;
    }
    if (error) {
        return <div className="alert alert-danger text-center">{error}</div>;
    }

    return (
        <div className="modal" tabIndex="-1" role="dialog">
    <div className="modal-dialog" role="document">
        <div className="modal-content">
            <div className="modal-header">
                <h5 className="modal-title">Update Employee</h5>
                <button
                    type="button"
                    className="close"
                    onClick={onClose}
                    aria-label="Close"
                >
                    <span aria-hidden="true">&times;</span>
                </button>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="modal-body">
                    {/* Form fields */}
                </div>
                <div className="modal-footer">
                    <button type="submit" className="btn btn-success">
                        Update
                    </button>
                    <button
                        type="button"
                        className="btn btn-secondary"
                        onClick={onClose}
                    >
                        Cancel
                    </button>
                </div>
            </form>
        </div>
    </div>
</div>

    );
};

export default UpdateEmployee;
